import React, { Component } from 'react'
import "./messageArea.css";
import $ from "jquery";

class MessageArea extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0
        }
    }
    
    getUserMessage() {
        const userMsg = $("#user_msg").val();
        $("#user_msg").val("");
        return userMsg;
    }

    render() { 
        return (  
            <div className="message-area"> 
                <div className="message-area-group-list"> 
                    <ul id="list-group">
                        {this.props.messages.map(message => 
                            <li key={message.username} className="list-group-item" >
                                {message.timestamp} <span key={message.username} style={{color: "#" + message.color}}>{message.username}</span> {message.message}</li>)
                        }
                    </ul>
                </div>
                <div className="message-area-form">
                    <input id="user_msg"></input>
                    <button className="btn btn-success" id="submit" onClick={() => this.props.onMessage(this.getUserMessage())}>Send</button>
                </div>
            </div>
        );
    }
}
 
export default MessageArea;
