import React, { Component } from 'react'
import "./messageArea.css";
import $ from "jquery";

class MessageArea extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: props.message,
        }
    }

    getUserMessage() {
        return $("#user_msg").val();
    }

    render() { 
        return (  
            <div className="message-area"> 
                <div className="message-area-group-list"> 
                    <ul id="list-group">
                        <li className="list-group-item">{this.state.message}</li>
                    </ul>
                </div>
                <div className="message-area-form">
                    <input id="user_msg"></input>
                    <button className="btn btn-success" id="submit" onClick={() => this.props.onClick(this.getUserMessage(this.getUserMessage()))}>Send</button>
                </div>
            </div>
        );
    }
}
 
export default MessageArea;
